// SPDX-License-Identifier: MIT OR LGPL-2.0-or-later
pragma solidity ^0.8.5;

import "./ERC20Token.sol";
import "./Operations.sol";
import "./Escrow.sol";
import "./Pausable.sol";

abstract contract Passengers is Pausable{
    using SafeMath for uint256;

    ERC20Token token;
    Operations operation;
    Escrow EscrowPassenger;

    uint256 REFUND_AMOUNT;

    mapping(address => uint256) internal passengerPaid;
    mapping(address => uint256 ) internal checkInCount;
    enum FlightStatus {PENDING, DELAYED, ONGOING, COMPLETED, CANCELLED}

    event transferedToEscrow(
        address indexed PassengerAddr,
        uint256 _amount,
        address indexed EscrowContract
    );

    event EscrowPassengerAdded(
        address indexed BookedBy,
        address indexed PassengerAddr,
        address indexed EscrowContract
    );

    event EscrowPassengerRemoved(
        address indexed AirlineAddr,
        address indexed PassengerAddr,
        Escrow indexed EscrowContract
    );

    event CancelledBooking(
        address indexed PassengerAddr,
        Escrow indexed EscrowContract
    );

    event PassengerClaimed(
        address indexed PassengerAddr,
        Escrow indexed EscrowContract,
        uint256 indexed AmountClaimed
    );

    modifier passengerCheck(address _flightEscrow, address _passenger, uint256 _keyId){
            require(operation.PassengerManifest(address(_flightEscrow), _passenger, _keyId).FlightID == address(_flightEscrow),
            "Passenger is not on the flight"
        );
        _;
    }


    modifier claimCheck(Escrow _flightEscrow, address _passenger, uint256 _keyId){
               require(operation.PassengerManifest(address(_flightEscrow), _passenger, _keyId).BookedBy == msg.sender,
               "Flgiht not booked by claimnant"
        );
        _;
    }

    modifier checkFlightStatus(Escrow _flightEscrow){
        require(
            _flightEscrow.getAirline().FlightStatus != uint32(FlightStatus(2)) || _flightEscrow.getAirline().FlightStatus != uint32(FlightStatus(3)),
            "Flight is ongoing or completed"
        );
        _;
    }

    constructor(address _tokenAddress, address _opAddress)

    {
        token = ERC20Token(_tokenAddress);
        operation = Operations(_opAddress);
    }

    /**
     * Transfer token to Escrow from Passengers
     * @param _flightEscrow Airline Flight Escrow address
     * @param _amount to be paid by the passenger
     * @param _flightClassPassenger Flight class
     * @param _flightStatusPassenger Status of the flight
     * @param _flightSeatPassenger Seat number of the passenger
     * @return bool
     */
    function transferToEscrow(
        Escrow _flightEscrow,
        address _airline,
        address _passenger,
        uint256 _amount,
        string memory _flightClassPassenger,
        uint32 _flightStatusPassenger,
        string memory _flightSeatPassenger
    )
    public 
    whenNotPaused
    returns (bool)
    {
        require(operation.IsUserList(msg.sender), "Not a User"); 
        require(token.balanceOf(msg.sender) >= _amount);
        token.transferToEscrow(msg.sender, address(_flightEscrow), _amount, _airline, _flightEscrow.getAirline().FlightRefund);

        passengerPaid[address(_flightEscrow)] += 1;
        passengerBooking(
                _flightEscrow,
                _passenger,
                 msg.sender,
                _flightClassPassenger,
                uint32(_amount),
                _flightStatusPassenger,
                _flightSeatPassenger,
                _flightEscrow.getAirline().FlightRefund
        );
        emit transferedToEscrow(
            msg.sender,
            _amount,
            address(_flightEscrow)
        );

        return true;
    }

    /**
     * Passenger Booking detail record
     */
    function passengerBooking(
        Escrow _flightEscrow,
        address _passenger,
        address _bookedby,
        string memory _flightClass,
        uint256 _flightCost,
        uint32 _flightStatus,
        string memory _flightSeat,
        uint256 _flightRefund
    )
    internal 
    returns(bool)
    {
        operation.FlightBookPassengers(
                _flightEscrow,
                _passenger,
                _bookedby,
                _flightClass,
                _flightCost,
                _flightStatus,
                _flightSeat,
                _flightRefund
        );
        emit EscrowPassengerAdded(msg.sender, _passenger, address(_flightEscrow));

    return true;
    }

    /**
     * CheckIn Passenger Checkin for boarding pass
     * @param _flightEscrow Airline Flight Escrow address
     */
    function checkIn(
        Escrow _flightEscrow,
        uint256 _keyId
    )
    public
    whenNotPaused
    passengerCheck(address(_flightEscrow), _msgSender(), _keyId)
    checkFlightStatus(_flightEscrow)
    returns (bool)
    {
        address flightEscrow = address(_flightEscrow);
         require(operation.PassengerManifest(flightEscrow,  _msgSender(), _keyId).FlightStatus != uint256(FlightStatus(4)));
         require(operation.PassengersCheckIn(flightEscrow, _msgSender(), _keyId));
         //checkIn count
         checkInCount[flightEscrow] += 1; 
        return true;
    }

    /**
     * PassengerClaim for passengers to claim their token from escrow
     * @param _flightEscrow Airline Flight Escrow address
     * @return bool
     */
    function passengerClaim(
        Escrow _flightEscrow,
        address _passenger,
        uint32 _keyId
    )
    internal 
    whenNotPaused
    passengerCheck(address(_flightEscrow), _passenger, _keyId)
    claimCheck(_flightEscrow, _passenger, _keyId)
    checkFlightStatus(_flightEscrow)
    returns (bool)
    {   
    address flightEscrow = address(_flightEscrow);
    require(!operation.PassengerManifest(flightEscrow, _passenger, _keyId).CheckedIn);
    require(operation.PassengerManifest(flightEscrow, _passenger, _keyId).FlightStatus == uint32(FlightStatus.CANCELLED));
    assert(operation.PassengerManifest(flightEscrow, _passenger, _keyId).FlightCostClaimed != 1); 

    //remove passenger claim amount
    operation.DeleteClaim(_flightEscrow, _passenger, _keyId);

    REFUND_AMOUNT =  operation.PassengerManifest(flightEscrow, _passenger, _keyId).FlightRefund;
    token.passengerClaimWithdrawal(flightEscrow, msg.sender, REFUND_AMOUNT);
    operation.Claims(flightEscrow, REFUND_AMOUNT);

    emit PassengerClaimed(msg.sender, _flightEscrow, REFUND_AMOUNT);

    return true;
    }

    /**
     * Cancel Flight booking by Passenger claim 30% their token from escrow
     * @param _flightEscrow Airline Flight Escrow address
     */
    function cancelBooking(
        Escrow _flightEscrow,
        uint256 _keyId
    )
    public
    passengerCheck(address(_flightEscrow), _msgSender(), _keyId)
    claimCheck(_flightEscrow, _msgSender(), _keyId)
    checkFlightStatus(_flightEscrow)
    returns (bool)
    {  

        //cancelled the booking
        operation.CancelPassengerBooking(
            address(_flightEscrow), 
            _msgSender(),
            _keyId,
            _flightEscrow.getAirline().BookingRefund,
            token.baseFee());

        passengerClaim(
            _flightEscrow, 
             _msgSender(),
             uint32(_keyId));

        emit CancelledBooking( _msgSender(), _flightEscrow);

        return true;
    }

}

